contracts/EscrowContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IVerifierMarketplace {
    function getVerifier(address verifier) external view returns (
        address verifierAddress,
        uint256 stakedAmount,
        uint256 reputationScore,
        uint256 totalVerifications,
        uint256 successfulVerifications,
        uint256 lastActivityTimestamp,
        bool isActive,
        string memory metadata,
        uint256 registrationTimestamp
    );

    function calculateVerificationFee(address verifier) external view returns (uint256);
}

interface IFraudDetection {
    function checkFraudScore(bytes32 requestId, bytes calldata verificationData) external returns (uint256 riskScore, bool isFraud);
}

/**
 * @title EscrowContract
 * @dev Smart contract for managing escrow funds during verification processes
 * Handles fund locking, release, refunds, and dispute resolution with AI fraud detection
 */
contract EscrowContract is 
    Initializable, 
    AccessControlUpgradeable, 
    PausableUpgradeable, 
    UUPSUpgradeable,
    ReentrancyGuardUpgradeable 
{
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant UPGRADER_ROLE = keccak256("UPGRADER_ROLE");
    bytes32 public constant ORACLE_ROLE = keccak256("ORACLE_ROLE");
    bytes32 public constant DISPUTE_RESOLVER_ROLE = keccak256("DISPUTE_RESOLVER_ROLE");

    enum EscrowStatus {
        Created,
        FundsLocked,
        VerificationSubmitted,
        FraudCheckPending,
        DisputeRaised,
        Completed,
        Refunded,
        Cancelled
    }

    enum DisputeStatus {
        None,
        Raised,
        UnderReview,
        Resolved
    }

    struct EscrowRequest {
        bytes32 requestId;
        address user;
        address verifier;
        uint256 amount;
        uint256 createdAt;
        uint256 expiresAt;
        EscrowStatus status;
        string verificationData; // Encrypted or hashed verification data
        uint256 fraudScore;
        bool fraudDetected;
        DisputeStatus disputeStatus;
        string disputeReason;
        address disputeResolver;
        uint256 resolvedAt;
    }

    struct DisputeInfo {
        bytes32 requestId;
        address initiator;
        string reason;
        uint256 createdAt;
        uint256 resolvedAt;
        address resolver;
        bool userFavored; // True if dispute resolved in favor of user
        string resolution;
    }

    // Contract references
    IVerifierMarketplace public verifierMarketplace;
    IFraudDetection public fraudDetection;
    IERC20 public paymentToken; // address(0) for native HBAR

    // Configuration
    uint256 public escrowTimeout; // Default timeout for escrow requests
    uint256 public disputeTimeout; // Timeout for dispute resolution
    uint256 public fraudThreshold; // Risk score threshold for fraud detection (0-100)
    uint256 public platformFeePercentage; // Platform fee in basis points
    address public feeRecipient; // Address to receive platform fees

    // Storage
    mapping(bytes32 => EscrowRequest) public escrowRequests;
    mapping(bytes32 => DisputeInfo) public disputes;
    mapping(address => uint256) public userRefunds; // Pending refunds for users
    mapping(address => uint256) public verifierEarnings; // Pending earnings for verifiers

    // Statistics
    uint256 public totalEscrowsCreated;
    uint256 public totalAmountEscrowed;
    uint256 public totalDisputesRaised;
    uint256 public totalFraudDetected;

    // Events
    event EscrowCreated(
        bytes32 indexed requestId,
        address indexed user,
        address indexed verifier,
        uint256 amount
    );

    event FundsLocked(
        bytes32 indexed requestId,
        uint256 amount,
        uint256 expiresAt
    );

    event VerificationSubmitted(
        bytes32 indexed requestId,
        address indexed verifier,
        string verificationData
    );

    event FraudCheckCompleted(
        bytes32 indexed requestId,
        uint256 fraudScore,
        bool fraudDetected
    );

    event DisputeRaised(
        bytes32 indexed requestId,
        address indexed initiator,
        string reason
    );

    event DisputeResolved(
        bytes32 indexed requestId,
        address indexed resolver,
        bool userFavored,
        string resolution
    );

    event FundsReleased(
        bytes32 indexed requestId,
        address indexed verifier,
        uint256 amount,
        uint256 platformFee
    );

    event RefundIssued(
        bytes32 indexed requestId,
        address indexed user,
        uint256 amount
    );

    event EscrowCancelled(
        bytes32 indexed requestId,
        string reason
    );

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        address admin,
        address _verifierMarketplace,
        address _fraudDetection,
        address _paymentToken,
        address _feeRecipient
    ) public initializer {
        __AccessControl_init();
        __Pausable_init();
        __UUPSUpgradeable_init();
        __ReentrancyGuard_init();

        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(ADMIN_ROLE, admin);
        _grantRole(UPGRADER_ROLE, admin);
        _grantRole(ORACLE_ROLE, admin);
        _grantRole(DISPUTE_RESOLVER_ROLE, admin);

        verifierMarketplace = IVerifierMarketplace(_verifierMarketplace);
        fraudDetection = IFraudDetection(_fraudDetection);
        paymentToken = IERC20(_paymentToken);
        feeRecipient = _feeRecipient;

        escrowTimeout = 7 days;
        disputeTimeout = 3 days;
        fraudThreshold = 70; // 70% risk score threshold
        platformFeePercentage = 250; // 2.5% platform fee
    }

    /**
     * @dev Create a new escrow request
     * @param requestId Unique identifier for the verification request
     * @param verifier Address of the assigned verifier
     */
    function createEscrow(
        bytes32 requestId,
        address verifier
    ) 
        external 
        payable 
        whenNotPaused 
        nonReentrant 
    {
        require(requestId != bytes32(0), "Invalid request ID");
        require(verifier != address(0), "Invalid verifier address");
        require(escrowRequests[requestId].requestId == bytes32(0), "Escrow already exists");

        // Verify verifier is active in marketplace
        (, , , , , , bool isActive, ,) = verifierMarketplace.getVerifier(verifier);
        require(isActive, "Verifier not active");

        // Calculate verification fee
        uint256 verificationFee = verifierMarketplace.calculateVerificationFee(verifier);

        uint256 amount;
        if (address(paymentToken) == address(0)) {
            // Native HBAR payment
            require(msg.value >= verificationFee, "Insufficient payment");
            amount = msg.value;
        } else {
            // ERC20 token payment
            require(msg.value == 0, "No native currency for token payment");
            amount = verificationFee;
            require(
                paymentToken.transferFrom(msg.sender, address(this), amount),
                "Payment transfer failed"
            );
        }

        escrowRequests[requestId] = EscrowRequest({
            requestId: requestId,
            user: msg.sender,
            verifier: verifier,
            amount: amount,
            createdAt: block.timestamp,
            expiresAt: block.timestamp + escrowTimeout,
            status: EscrowStatus.Created,
            verificationData: "",
            fraudScore: 0,
            fraudDetected: false,
            disputeStatus: DisputeStatus.None,
            disputeReason: "",
            disputeResolver: address(0),
            resolvedAt: 0
        });

        totalEscrowsCreated++;
        totalAmountEscrowed += amount;

        emit EscrowCreated(requestId, msg.sender, verifier, amount);
    }

    /**
     * @dev Lock funds for verification process
     * @param requestId The escrow request ID
     */
    function lockFunds(bytes32 requestId) 
        external 
        onlyRole(ORACLE_ROLE) 
        whenNotPaused 
    {
        EscrowRequest storage request = escrowRequests[requestId];
        require(request.status == EscrowStatus.Created, "Invalid status for locking funds");
        require(block.timestamp <= request.expiresAt, "Escrow expired");

        request.status = EscrowStatus.FundsLocked;

        emit FundsLocked(requestId, request.amount, request.expiresAt);
    }

    /**
     * @dev Submit verification result
     * @param requestId The escrow request ID
     * @param verificationData Encrypted or hashed verification data
     */
    function submitVerification(
        bytes32 requestId,
        string calldata verificationData
    ) 
        external 
        whenNotPaused 
    {
        EscrowRequest storage request = escrowRequests[requestId];
        require(msg.sender == request.verifier, "Only assigned verifier can submit");
        require(request.status == EscrowStatus.FundsLocked, "Invalid status for submission");
        require(block.timestamp <= request.expiresAt, "Escrow expired");
        require(bytes(verificationData).length > 0, "Verification data cannot be empty");

        request.verificationData = verificationData;
        request.status = EscrowStatus.VerificationSubmitted;

        emit VerificationSubmitted(requestId, msg.sender, verificationData);

        // Trigger fraud detection
        _triggerFraudCheck(requestId);
    }

    /**
     * @dev Complete fraud check and update escrow status
     * @param requestId The escrow request ID
     * @param riskScore Risk score from fraud detection (0-100)
     * @param isFraud Whether fraud was detected
     */
    function completeFraudCheck(
        bytes32 requestId,
        uint256 riskScore,
        bool isFraud
    ) 
        external 
        onlyRole(ORACLE_ROLE) 
        whenNotPaused 
    {
        EscrowRequest storage request = escrowRequests[requestId];
        require(request.status == EscrowStatus.FraudCheckPending, "Invalid status for fraud check");

        request.fraudScore = riskScore;
        request.fraudDetected = isFraud;

        if (isFraud) {
            totalFraudDetected++;
            // Automatically refund user if fraud detected
            _issueRefund(requestId, "Fraud detected by AI system");
        } else {
            // Release funds to verifier
            _releaseFunds(requestId);
        }

        emit FraudCheckCompleted(requestId, riskScore, isFraud);
    }

    /**
     * @dev Raise a dispute
     * @param requestId The escrow request ID
     * @param reason Reason for the dispute
     */
    function raiseDispute(
        bytes32 requestId,
        string calldata reason
    ) 
        external 
        whenNotPaused 
    {
        EscrowRequest storage request = escrowRequests[requestId];
        require(
            msg.sender == request.user || msg.sender == request.verifier,
            "Only user or verifier can raise dispute"
        );
        require(
            request.status == EscrowStatus.VerificationSubmitted || 
            request.status == EscrowStatus.Completed,
            "Invalid status for dispute"
        );
        require(request.disputeStatus == DisputeStatus.None, "Dispute already raised");
        require(bytes(reason).length > 0, "Dispute reason cannot be empty");

        request.disputeStatus = DisputeStatus.Raised;
        request.disputeReason = reason;
        request.status = EscrowStatus.DisputeRaised;

        disputes[requestId] = DisputeInfo({
            requestId: requestId,
            initiator: msg.sender,
            reason: reason,
            createdAt: block.timestamp,
            resolvedAt: 0,
            resolver: address(0),
            userFavored: false,
            resolution: ""
        });

        totalDisputesRaised++;

        emit DisputeRaised(requestId, msg.sender, reason);
    }

    /**
     * @dev Resolve a dispute
     * @param requestId The escrow request ID
     * @param userFavored Whether the dispute is resolved in favor of the user
     * @param resolution Resolution details
     */
    function resolveDispute(
        bytes32 requestId,
        bool userFavored,
        string calldata resolution
    ) 
        external 
        onlyRole(DISPUTE_RESOLVER_ROLE) 
        whenNotPaused 
    {
        EscrowRequest storage request = escrowRequests[requestId];
        require(request.status == EscrowStatus.DisputeRaised, "No active dispute");
        require(request.disputeStatus == DisputeStatus.Raised, "Dispute not in correct state");

        DisputeInfo storage dispute = disputes[requestId];
        dispute.resolver = msg.sender;
        dispute.resolvedAt = block.timestamp;
        dispute.userFavored = userFavored;
        dispute.resolution = resolution;

        request.disputeStatus = DisputeStatus.Resolved;
        request.disputeResolver = msg.sender;
        request.resolvedAt = block.timestamp;

        if (userFavored) {
            _issueRefund(requestId, "Dispute resolved in favor of user");
        } else {
            _releaseFunds(requestId);
        }

        emit DisputeResolved(requestId, msg.sender, userFavored, resolution);
    }

    /**
     * @dev Cancel an expired escrow
     * @param requestId The escrow request ID
     */
    function cancelExpiredEscrow(bytes32 requestId) 
        external 
        whenNotPaused 
    {
        EscrowRequest storage request = escrowRequests[requestId];
        require(block.timestamp > request.expiresAt, "Escrow not expired");
        require(
            request.status == EscrowStatus.Created || 
            request.status == EscrowStatus.FundsLocked,
            "Cannot cancel escrow in current status"
        );

        _issueRefund(requestId, "Escrow expired");

        emit EscrowCancelled(requestId, "Escrow expired");
    }

    /**
     * @dev Withdraw pending refunds
     */
    function withdrawRefund() 
        external 
        whenNotPaused 
        nonReentrant 
    {
        uint256 refundAmount = userRefunds[msg.sender];
        require(refundAmount > 0, "No refund available");

        userRefunds[msg.sender] = 0;

        _transferFunds(msg.sender, refundAmount);
    }

    /**
     * @dev Withdraw verifier earnings
     */
    function withdrawEarnings() 
        external 
        whenNotPaused 
        nonReentrant 
    {
        uint256 earnings = verifierEarnings[msg.sender];
        require(earnings > 0, "No earnings available");

        verifierEarnings[msg.sender] = 0;

        _transferFunds(msg.sender, earnings);
    }

    /**
     * @dev Get escrow request details
     * @param requestId The escrow request ID
     * @return EscrowRequest struct
     */
    function getEscrowRequest(bytes32 requestId) 
        external 
        view 
        returns (EscrowRequest memory) 
    {
        return escrowRequests[requestId];
    }

    /**
     * @dev Get dispute information
     * @param requestId The escrow request ID
     * @return DisputeInfo struct
     */
    function getDispute(bytes32 requestId) 
        external 
        view 
        returns (DisputeInfo memory) 
    {
        return disputes[requestId];
    }

    /**
     * @dev Internal function to trigger fraud check
     * @param requestId The escrow request ID
     */
    function _triggerFraudCheck(bytes32 requestId) internal {
        EscrowRequest storage request = escrowRequests[requestId];
        request.status = EscrowStatus.FraudCheckPending;

        // In a real implementation, this would trigger an external fraud detection service
        // For now, we'll emit an event that can be picked up by off-chain services
    }

    /**
     * @dev Internal function to release funds to verifier
     * @param requestId The escrow request ID
     */
    function _releaseFunds(bytes32 requestId) internal {
        EscrowRequest storage request = escrowRequests[requestId];

        uint256 platformFee = (request.amount * platformFeePercentage) / 10000;
        uint256 verifierAmount = request.amount - platformFee;

        verifierEarnings[request.verifier] += verifierAmount;

        if (platformFee > 0 && feeRecipient != address(0)) {
            _transferFunds(feeRecipient, platformFee);
        }

        request.status = EscrowStatus.Completed;

        emit FundsReleased(requestId, request.verifier, verifierAmount, platformFee);
    }

    /**
     * @dev Internal function to issue refund to user
     * @param requestId The escrow request ID
     * @param reason Reason for refund
     */
    function _issueRefund(bytes32 requestId, string memory reason) internal {
        EscrowRequest storage request = escrowRequests[requestId];

        userRefunds[request.user] += request.amount;
        request.status = EscrowStatus.Refunded;

        emit RefundIssued(requestId, request.user, request.amount);
    }

    /**
     * @dev Internal function to transfer funds
     * @param to Recipient address
     * @param amount Amount to transfer
     */
    function _transferFunds(address to, uint256 amount) internal {
        if (address(paymentToken) == address(0)) {
            payable(to).transfer(amount);
        } else {
            require(paymentToken.transfer(to, amount), "Transfer failed");
        }
    }

    /**
     * @dev Admin functions
     */
    function updateEscrowTimeout(uint256 _escrowTimeout) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        escrowTimeout = _escrowTimeout;
    }

    function updateDisputeTimeout(uint256 _disputeTimeout) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        disputeTimeout = _disputeTimeout;
    }

    function updateFraudThreshold(uint256 _fraudThreshold) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        require(_fraudThreshold <= 100, "Invalid fraud threshold");
        fraudThreshold = _fraudThreshold;
    }

    function updatePlatformFee(uint256 _platformFeePercentage) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        require(_platformFeePercentage <= 1000, "Platform fee too high"); // Max 10%
        platformFeePercentage = _platformFeePercentage;
    }

    function updateFeeRecipient(address _feeRecipient) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        feeRecipient = _feeRecipient;
    }

    /**
     * @dev Pause/unpause contract
     */
    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @dev Authorize upgrade
     */
    function _authorizeUpgrade(address newImplementation) 
        internal 
        onlyRole(UPGRADER_ROLE) 
        override 
    {}

    /**
     * @dev Emergency withdrawal function (only admin)
     */
    function emergencyWithdraw() 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
        nonReentrant 
    {
        if (address(paymentToken) == address(0)) {
            payable(msg.sender).transfer(address(this).balance);
        } else {
            uint256 balance = paymentToken.balanceOf(address(this));
            require(paymentToken.transfer(msg.sender, balance), "Transfer failed");
        }
    }

    /**
     * @dev Receive function for native currency deposits
     */
    receive() external payable {
        // Allow contract to receive native currency
    }
}

contracts/VCRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721URIStorageUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";

/**
 * @title VCRegistry
 * @dev Enhanced smart contract for managing Verifiable Credentials as Soulbound Tokens (SBTs)
 * Supports Hedera DID integration, non-transferable NFTs, and advanced credential management
 */
contract VCRegistry is 
    Initializable, 
    AccessControlUpgradeable, 
    PausableUpgradeable, 
    UUPSUpgradeable,
    ERC721Upgradeable,
    ERC721URIStorageUpgradeable,
    ReentrancyGuardUpgradeable 
{
    using CountersUpgradeable for CountersUpgradeable.Counter;

    bytes32 public constant ISSUER_ROLE = keccak256("ISSUER_ROLE");
    bytes32 public constant UPGRADER_ROLE = keccak256("UPGRADER_ROLE");
    bytes32 public constant DID_RESOLVER_ROLE = keccak256("DID_RESOLVER_ROLE");

    enum CredentialType {
        Identity,
        Education,
        Professional,
        Financial,
        Health,
        Government,
        Custom
    }

    enum CredentialStatus {
        Active,
        Suspended,
        Revoked,
        Expired
    }

    struct CredentialRecord {
        uint256 tokenId;
        address issuer;
        address holder;
        string hederaDID; // Hedera DID of the credential holder
        bytes32 vcHash; // Hash of the verifiable credential
        CredentialType credentialType;
        CredentialStatus status;
        uint64 issuedAt;
        uint64 expiresAt;
        uint64 revokedAt;
        string metadataURI; // IPFS or Hedera File Service URI
        string schemaURI; // URI to credential schema
        bytes32[] claims; // Array of claim hashes
        mapping(string => string) attributes; // Dynamic attributes
    }

    struct DIDDocument {
        string did;
        address controller;
        string[] verificationMethods;
        string[] services;
        uint64 created;
        uint64 updated;
        bool active;
    }

    // Token ID counter
    CountersUpgradeable.Counter private _tokenIdCounter;

    // Mapping from token ID to credential record
    mapping(uint256 => CredentialRecord) public credentials;

    // Mapping from VC hash to token ID
    mapping(bytes32 => uint256) public vcHashToTokenId;

    // Mapping from Hedera DID to token IDs
    mapping(string => uint256[]) public didToTokenIds;

    // Mapping from Hedera DID to DID Document
    mapping(string => DIDDocument) public didDocuments;

    // Mapping to track authorized issuers
    mapping(address => bool) public authorizedIssuers;

    // Mapping from issuer to issued credential count
    mapping(address => uint256) public issuerCredentialCount;

    // Mapping from credential type to count
    mapping(CredentialType => uint256) public credentialTypeCount;

    // Mapping to track credential schemas
    mapping(string => bool) public approvedSchemas;

    // Contract configuration
    uint256 public maxCredentialsPerDID;
    uint256 public defaultExpirationPeriod;
    bool public transfersEnabled; // For emergency transfers only

    // Events
    event CredentialIssued(
        uint256 indexed tokenId,
        bytes32 indexed vcHash,
        address indexed issuer,
        address holder,
        string hederaDID,
        CredentialType credentialType,
        uint64 issuedAt,
        uint64 expiresAt
    );

    event CredentialRevoked(
        uint256 indexed tokenId,
        bytes32 indexed vcHash,
        address indexed issuer,
        uint64 revokedAt,
        string reason
    );

    event CredentialSuspended(
        uint256 indexed tokenId,
        address indexed issuer,
        string reason
    );

    event CredentialReactivated(
        uint256 indexed tokenId,
        address indexed issuer
    );

    event DIDRegistered(
        string indexed did,
        address indexed controller,
        uint64 timestamp
    );

    event DIDUpdated(
        string indexed did,
        address indexed controller,
        uint64 timestamp
    );

    event DIDDeactivated(
        string indexed did,
        address indexed controller,
        uint64 timestamp
    );

    event SchemaApproved(
        string indexed schemaURI,
        address indexed approver
    );

    event IssuerAdded(address indexed issuer);
    event IssuerRemoved(address indexed issuer);

    event TransferAttemptBlocked(
        address indexed from,
        address indexed to,
        uint256 indexed tokenId
    );

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        address admin,
        string memory name,
        string memory symbol
    ) public initializer {
        __AccessControl_init();
        __Pausable_init();
        __UUPSUpgradeable_init();
        __ERC721_init(name, symbol);
        __ERC721URIStorage_init();
        __ReentrancyGuard_init();

        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(UPGRADER_ROLE, admin);
        _grantRole(DID_RESOLVER_ROLE, admin);

        // Set default configuration
        maxCredentialsPerDID = 10;
        defaultExpirationPeriod = 365 days;
        transfersEnabled = false;

        // Start token IDs from 1
        _tokenIdCounter.increment();
    }

    /**
     * @dev Register a Hedera DID
     * @param did The Hedera DID string
     * @param controller The controller address
     * @param verificationMethods Array of verification method URIs
     * @param services Array of service endpoint URIs
     */
    function registerDID(
        string calldata did,
        address controller,
        string[] calldata verificationMethods,
        string[] calldata services
    ) 
        external 
        whenNotPaused 
        onlyRole(DID_RESOLVER_ROLE) 
    {
        require(bytes(did).length > 0, "VCRegistry: Invalid DID");
        require(controller != address(0), "VCRegistry: Invalid controller");
        require(!didDocuments[did].active, "VCRegistry: DID already registered");

        didDocuments[did] = DIDDocument({
            did: did,
            controller: controller,
            verificationMethods: verificationMethods,
            services: services,
            created: uint64(block.timestamp),
            updated: uint64(block.timestamp),
            active: true
        });

        emit DIDRegistered(did, controller, uint64(block.timestamp));
    }

    /**
     * @dev Issue a Verifiable Credential as a Soulbound Token
     * @param holder The credential holder address
     * @param hederaDID The Hedera DID of the holder
     * @param vcHash The SHA256 hash of the normalized VC
     * @param credentialType The type of credential
     * @param metadataURI The URI for credential metadata
     * @param schemaURI The URI for credential schema
     * @param claims Array of claim hashes
     * @param expirationPeriod Expiration period in seconds (0 for no expiration)
     */
    function issueCredential(
        address holder,
        string calldata hederaDID,
        bytes32 vcHash,
        CredentialType credentialType,
        string calldata metadataURI,
        string calldata schemaURI,
        bytes32[] calldata claims,
        uint256 expirationPeriod
    ) 
        external 
        whenNotPaused 
        onlyRole(ISSUER_ROLE)
        nonReentrant
    {
        require(holder != address(0), "VCRegistry: Invalid holder address");
        require(bytes(hederaDID).length > 0, "VCRegistry: Invalid Hedera DID");
        require(vcHash != bytes32(0), "VCRegistry: Invalid VC hash");
        require(bytes(metadataURI).length > 0, "VCRegistry: Metadata URI cannot be empty");
        require(vcHashToTokenId[vcHash] == 0, "VCRegistry: VC already issued");
        require(didDocuments[hederaDID].active, "VCRegistry: DID not registered or inactive");
        require(didToTokenIds[hederaDID].length < maxCredentialsPerDID, "VCRegistry: Max credentials per DID exceeded");

        // Validate schema if provided
        if (bytes(schemaURI).length > 0) {
            require(approvedSchemas[schemaURI], "VCRegistry: Schema not approved");
        }

        uint256 tokenId = _tokenIdCounter.current();
        _tokenIdCounter.increment();

        // Calculate expiration
        uint64 expiresAt = 0;
        if (expirationPeriod > 0) {
            expiresAt = uint64(block.timestamp + expirationPeriod);
        } else if (defaultExpirationPeriod > 0) {
            expiresAt = uint64(block.timestamp + defaultExpirationPeriod);
        }

        // Create credential record
        CredentialRecord storage credential = credentials[tokenId];
        credential.tokenId = tokenId;
        credential.issuer = msg.sender;
        credential.holder = holder;
        credential.hederaDID = hederaDID;
        credential.vcHash = vcHash;
        credential.credentialType = credentialType;
        credential.status = CredentialStatus.Active;
        credential.issuedAt = uint64(block.timestamp);
        credential.expiresAt = expiresAt;
        credential.revokedAt = 0;
        credential.metadataURI = metadataURI;
        credential.schemaURI = schemaURI;
        credential.claims = claims;

        // Update mappings
        vcHashToTokenId[vcHash] = tokenId;
        didToTokenIds[hederaDID].push(tokenId);
        issuerCredentialCount[msg.sender]++;
        credentialTypeCount[credentialType]++;

        // Mint the soulbound token
        _safeMint(holder, tokenId);
        _setTokenURI(tokenId, metadataURI);

        emit CredentialIssued(
            tokenId,
            vcHash,
            msg.sender,
            holder,
            hederaDID,
            credentialType,
            uint64(block.timestamp),
            expiresAt
        );
    }

    /**
     * @dev Revoke a Verifiable Credential
     * @param tokenId The token ID of the credential to revoke
     * @param reason Reason for revocation
     */
    function revokeCredential(uint256 tokenId, string calldata reason) 
        external 
        whenNotPaused 
    {
        require(_exists(tokenId), "VCRegistry: Token does not exist");
        CredentialRecord storage credential = credentials[tokenId];
        require(
            credential.issuer == msg.sender || hasRole(DEFAULT_ADMIN_ROLE, msg.sender),
            "VCRegistry: Only issuer or admin can revoke"
        );
        require(credential.status != CredentialStatus.Revoked, "VCRegistry: Credential already revoked");

        credential.status = CredentialStatus.Revoked;
        credential.revokedAt = uint64(block.timestamp);

        emit CredentialRevoked(
            tokenId,
            credential.vcHash,
            credential.issuer,
            uint64(block.timestamp),
            reason
        );
    }

    /**
     * @dev Suspend a Verifiable Credential
     * @param tokenId The token ID of the credential to suspend
     * @param reason Reason for suspension
     */
    function suspendCredential(uint256 tokenId, string calldata reason) 
        external 
        whenNotPaused 
    {
        require(_exists(tokenId), "VCRegistry: Token does not exist");
        CredentialRecord storage credential = credentials[tokenId];
        require(
            credential.issuer == msg.sender || hasRole(DEFAULT_ADMIN_ROLE, msg.sender),
            "VCRegistry: Only issuer or admin can suspend"
        );
        require(credential.status == CredentialStatus.Active, "VCRegistry: Credential not active");

        credential.status = CredentialStatus.Suspended;

        emit CredentialSuspended(tokenId, credential.issuer, reason);
    }

    /**
     * @dev Reactivate a suspended Verifiable Credential
     * @param tokenId The token ID of the credential to reactivate
     */
    function reactivateCredential(uint256 tokenId) 
        external 
        whenNotPaused 
    {
        require(_exists(tokenId), "VCRegistry: Token does not exist");
        CredentialRecord storage credential = credentials[tokenId];
        require(
            credential.issuer == msg.sender || hasRole(DEFAULT_ADMIN_ROLE, msg.sender),
            "VCRegistry: Only issuer or admin can reactivate"
        );
        require(credential.status == CredentialStatus.Suspended, "VCRegistry: Credential not suspended");

        // Check if credential has expired
        if (credential.expiresAt > 0 && block.timestamp > credential.expiresAt) {
            credential.status = CredentialStatus.Expired;
        } else {
            credential.status = CredentialStatus.Active;
        }

        emit CredentialReactivated(tokenId, credential.issuer);
    }

    /**
     * @dev Set credential attributes
     * @param tokenId The token ID of the credential
     * @param key The attribute key
     * @param value The attribute value
     */
    function setCredentialAttribute(
        uint256 tokenId,
        string calldata key,
        string calldata value
    ) 
        external 
        whenNotPaused 
    {
        require(_exists(tokenId), "VCRegistry: Token does not exist");
        CredentialRecord storage credential = credentials[tokenId];
        require(
            credential.issuer == msg.sender || hasRole(DEFAULT_ADMIN_ROLE, msg.sender),
            "VCRegistry: Only issuer or admin can set attributes"
        );

        credential.attributes[key] = value;
    }

    /**
     * @dev Check if a credential is valid and active
     * @param tokenId The token ID of the credential
     * @return valid True if credential is active and not expired
     * @return status Current status of the credential
     * @return issuedAt Timestamp when credential was issued
     * @return expiresAt Timestamp when credential expires (0 if no expiration)
     * @return revokedAt Timestamp when credential was revoked (0 if not revoked)
     */
    function isCredentialValid(uint256 tokenId) 
        external 
        view 
        returns (
            bool valid,
            CredentialStatus status,
            uint64 issuedAt,
            uint64 expiresAt,
            uint64 revokedAt
        ) 
    {
        if (!_exists(tokenId)) {
            return (false, CredentialStatus.Revoked, 0, 0, 0);
        }

        CredentialRecord storage credential = credentials[tokenId];

        // Check if expired
        if (credential.expiresAt > 0 && block.timestamp > credential.expiresAt) {
            return (false, CredentialStatus.Expired, credential.issuedAt, credential.expiresAt, credential.revokedAt);
        }

        bool isValid = credential.status == CredentialStatus.Active;

        return (
            isValid,
            credential.status,
            credential.issuedAt,
            credential.expiresAt,
            credential.revokedAt
        );
    }

    /**
     * @dev Get credential by VC hash
     * @param vcHash The VC hash
     * @return tokenId The token ID (0 if not found)
     * @return exists Whether the credential exists
     */
    function getCredentialByHash(bytes32 vcHash) 
        external 
        view 
        returns (uint256 tokenId, bool exists) 
    {
        tokenId = vcHashToTokenId[vcHash];
        exists = tokenId != 0 && _exists(tokenId);
    }

    /**
     * @dev Get credentials by Hedera DID
     * @param hederaDID The Hedera DID
     * @return tokenIds Array of token IDs associated with the DID
     */
    function getCredentialsByDID(string calldata hederaDID) 
        external 
        view 
        returns (uint256[] memory tokenIds) 
    {
        return didToTokenIds[hederaDID];
    }

    /**
     * @dev Get credential attribute
     * @param tokenId The token ID
     * @param key The attribute key
     * @return value The attribute value
     */
    function getCredentialAttribute(uint256 tokenId, string calldata key) 
        external 
        view 
        returns (string memory value) 
    {
        require(_exists(tokenId), "VCRegistry: Token does not exist");
        return credentials[tokenId].attributes[key];
    }

    /**
     * @dev Get DID document
     * @param did The Hedera DID
     * @return document The DID document
     */
    function getDIDDocument(string calldata did) 
        external 
        view 
        returns (DIDDocument memory document) 
    {
        return didDocuments[did];
    }

    /**
     * @dev Override transfer functions to make tokens soulbound (non-transferable)
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId,
        uint256 batchSize
    ) internal virtual override {
        super._beforeTokenTransfer(from, to, tokenId, batchSize);

        // Allow minting (from == address(0)) and burning (to == address(0))
        if (from != address(0) && to != address(0)) {
            // Only allow transfers if explicitly enabled (for emergency situations)
            if (!transfersEnabled) {
                emit TransferAttemptBlocked(from, to, tokenId);
                revert("VCRegistry: Soulbound tokens are non-transferable");
            }

            // Even if transfers are enabled, require admin approval
            require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "VCRegistry: Transfer requires admin approval");
        }
    }

    /**
     * @dev Override approve to prevent approvals (soulbound tokens)
     */
    function approve(address to, uint256 tokenId) public virtual override(ERC721Upgradeable, IERC721Upgradeable) {
        revert("VCRegistry: Soulbound tokens cannot be approved");
    }

    /**
     * @dev Override setApprovalForAll to prevent approvals (soulbound tokens)
     */
    function setApprovalForAll(address operator, bool approved) public virtual override(ERC721Upgradeable, IERC721Upgradeable) {
        revert("VCRegistry: Soulbound tokens cannot be approved");
    }

    /**
     * @dev Register a new issuer
     * @param issuer Address of the issuer to register
     */
    function registerIssuer(address issuer) 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        require(issuer != address(0), "VCRegistry: Invalid issuer address");

        _grantRole(ISSUER_ROLE, issuer);
        authorizedIssuers[issuer] = true;

        emit IssuerAdded(issuer);
    }

    /**
     * @dev Revoke issuer privileges
     * @param issuer Address of the issuer to revoke
     */
    function revokeIssuer(address issuer) 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        require(issuer != address(0), "VCRegistry: Invalid issuer address");

        _revokeRole(ISSUER_ROLE, issuer);
        authorizedIssuers[issuer] = false;

        emit IssuerRemoved(issuer);
    }

    /**
     * @dev Pause the contract
     */
    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    /**
     * @dev Unpause the contract
     */
    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @dev Get credential record by token ID
     * @param tokenId The token ID
     * @return _tokenId The token ID
     * @return issuer The issuer address
     * @return holder The holder address
     * @return hederaDID The Hedera DID
     * @return vcHash The credential hash
     */
    function getCredential(uint256 tokenId) 
        external 
        view 
        returns (
            uint256 _tokenId,
            address issuer,
            address holder,
            string memory hederaDID,
            bytes32 vcHash,
            CredentialType credentialType,
            CredentialStatus status,
            uint64 issuedAt,
            uint64 expiresAt,
            uint64 revokedAt,
            string memory metadataURI,
            string memory schemaURI,
            bytes32[] memory claims
        ) 
    {
        require(_exists(tokenId), "VCRegistry: Token does not exist");
        CredentialRecord storage credential = credentials[tokenId];

        return (
            credential.tokenId,
            credential.issuer,
            credential.holder,
            credential.hederaDID,
            credential.vcHash,
            credential.credentialType,
            credential.status,
            credential.issuedAt,
            credential.expiresAt,
            credential.revokedAt,
            credential.metadataURI,
            credential.schemaURI,
            credential.claims
        );
    }

    /**
     * @dev Approve a credential schema
     * @param schemaURI The schema URI to approve
     */
    function approveSchema(string calldata schemaURI) 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        require(bytes(schemaURI).length > 0, "VCRegistry: Invalid schema URI");
        approvedSchemas[schemaURI] = true;

        emit SchemaApproved(schemaURI, msg.sender);
    }

    /**
     * @dev Revoke approval for a credential schema
     * @param schemaURI The schema URI to revoke
     */
    function revokeSchemaApproval(string calldata schemaURI) 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        approvedSchemas[schemaURI] = false;
    }

    /**
     * @dev Update DID document
     * @param did The Hedera DID
     * @param verificationMethods New verification methods
     * @param services New services
     */
    function updateDID(
        string calldata did,
        string[] calldata verificationMethods,
        string[] calldata services
    ) 
        external 
        whenNotPaused 
        onlyRole(DID_RESOLVER_ROLE) 
    {
        require(didDocuments[did].active, "VCRegistry: DID not registered or inactive");

        DIDDocument storage document = didDocuments[did];
        document.verificationMethods = verificationMethods;
        document.services = services;
        document.updated = uint64(block.timestamp);

        emit DIDUpdated(did, document.controller, uint64(block.timestamp));
    }

    /**
     * @dev Deactivate a DID
     * @param did The Hedera DID to deactivate
     */
    function deactivateDID(string calldata did) 
        external 
        whenNotPaused 
        onlyRole(DID_RESOLVER_ROLE) 
    {
        require(didDocuments[did].active, "VCRegistry: DID not active");

        didDocuments[did].active = false;
        didDocuments[did].updated = uint64(block.timestamp);

        emit DIDDeactivated(did, didDocuments[did].controller, uint64(block.timestamp));
    }

    /**
     * @dev Update contract configuration
     */
    function updateMaxCredentialsPerDID(uint256 _maxCredentialsPerDID) 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        maxCredentialsPerDID = _maxCredentialsPerDID;
    }

    function updateDefaultExpirationPeriod(uint256 _defaultExpirationPeriod) 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        defaultExpirationPeriod = _defaultExpirationPeriod;
    }

    function setTransfersEnabled(bool _transfersEnabled) 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        transfersEnabled = _transfersEnabled;
    }

    /**
     * @dev Get contract statistics
     */
    function getContractStats() 
        external 
        view 
        returns (
            uint256 totalCredentials,
            uint256 totalIssuers,
            uint256 totalDIDs,
            uint256[7] memory credentialTypeCounts
        ) 
    {
        totalCredentials = _tokenIdCounter.current() - 1;

        // Count active issuers
        // Note: This is a simplified count, in practice you might want to maintain a separate counter

        // Count active DIDs
        // Note: This would require maintaining a separate counter for efficiency

        // Get credential type counts
        for (uint i = 0; i < 7; i++) {
            credentialTypeCounts[i] = credentialTypeCount[CredentialType(i)];
        }

        return (totalCredentials, 0, 0, credentialTypeCounts); // Simplified return
    }

    /**
     * @dev Override tokenURI to support both ERC721URIStorage and custom logic
     */
    function tokenURI(uint256 tokenId) 
        public 
        view 
        virtual 
        override(ERC721Upgradeable, ERC721URIStorageUpgradeable) 
        returns (string memory) 
    {
        return ERC721URIStorageUpgradeable.tokenURI(tokenId);
    }

    /**
     * @dev Override supportsInterface
     */
    function supportsInterface(bytes4 interfaceId) 
        public 
        view 
        virtual 
        override(ERC721Upgradeable, AccessControlUpgradeable, ERC721URIStorageUpgradeable) 
        returns (bool) 
    {
        return super.supportsInterface(interfaceId);
    }

    /**
     * @dev Override _burn to handle URI storage
     */
    function _burn(uint256 tokenId) 
        internal 
        virtual 
        override(ERC721Upgradeable, ERC721URIStorageUpgradeable) 
    {
        super._burn(tokenId);
    }

    /**
     * @dev Check if an address is an authorized issuer
     * @param issuer Address to check
     * @return True if the address is an authorized issuer
     */
    function isAuthorizedIssuer(address issuer) 
        external 
        view 
        returns (bool) 
    {
        return hasRole(ISSUER_ROLE, issuer);
    }

    function _authorizeUpgrade(address newImplementation)
        internal
        onlyRole(UPGRADER_ROLE)
        override
    {}
}

contracts/VerifierMarketplace.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title VerifierMarketplace
 * @dev Smart contract for managing verifiers with reputation, staking, and dynamic pricing
 * Verifiers must stake HBAR or stablecoin to participate in the verification marketplace
 */
contract VerifierMarketplace is 
    Initializable, 
    AccessControlUpgradeable, 
    PausableUpgradeable, 
    UUPSUpgradeable,
    ReentrancyGuardUpgradeable 
{
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant UPGRADER_ROLE = keccak256("UPGRADER_ROLE");
    bytes32 public constant SLASHER_ROLE = keccak256("SLASHER_ROLE");

    // Minimum stake required to become a verifier (in wei)
    uint256 public minimumStake;

    // Base verification fee (in wei)
    uint256 public baseVerificationFee;

    // Reputation multiplier for pricing (basis points, 10000 = 100%)
    uint256 public reputationMultiplier;

    // Slashing percentage for fraud/inactivity (basis points)
    uint256 public slashingPercentage;

    // Inactivity threshold in seconds
    uint256 public inactivityThreshold;

    // Supported staking token (address(0) for native HBAR)
    IERC20 public stakingToken;

    struct Verifier {
        address verifierAddress;
        uint256 stakedAmount;
        uint256 reputationScore; // 0-10000 (basis points)
        uint256 totalVerifications;
        uint256 successfulVerifications;
        uint256 lastActivityTimestamp;
        bool isActive;
        string metadata; // IPFS hash or JSON metadata
        uint256 registrationTimestamp;
    }

    struct VerificationRequest {
        address requester;
        address assignedVerifier;
        uint256 fee;
        uint256 timestamp;
        bool completed;
        bool successful;
        string requestData; // Encrypted or hashed request data
    }

    // Mapping from verifier address to verifier info
    mapping(address => Verifier) public verifiers;

    // Array of all verifier addresses for enumeration
    address[] public verifierList;

    // Mapping from request ID to verification request
    mapping(bytes32 => VerificationRequest) public verificationRequests;

    // Mapping to track verifier earnings
    mapping(address => uint256) public verifierEarnings;

    // Events
    event VerifierRegistered(
        address indexed verifier,
        uint256 stakedAmount,
        string metadata
    );

    event VerifierStakeIncreased(
        address indexed verifier,
        uint256 additionalAmount,
        uint256 totalStake
    );

    event VerifierSlashed(
        address indexed verifier,
        uint256 slashedAmount,
        string reason
    );

    event VerificationCompleted(
        bytes32 indexed requestId,
        address indexed verifier,
        bool successful,
        uint256 fee
    );

    event ReputationUpdated(
        address indexed verifier,
        uint256 oldScore,
        uint256 newScore
    );

    event VerifierDeactivated(
        address indexed verifier,
        string reason
    );

    event EarningsWithdrawn(
        address indexed verifier,
        uint256 amount
    );

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    function initialize(
        address admin,
        uint256 _minimumStake,
        uint256 _baseVerificationFee,
        address _stakingToken
    ) public initializer {
        __AccessControl_init();
        __Pausable_init();
        __UUPSUpgradeable_init();
        __ReentrancyGuard_init();

        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(ADMIN_ROLE, admin);
        _grantRole(UPGRADER_ROLE, admin);
        _grantRole(SLASHER_ROLE, admin);

        minimumStake = _minimumStake;
        baseVerificationFee = _baseVerificationFee;
        reputationMultiplier = 5000; // 50% impact on pricing
        slashingPercentage = 1000; // 10% slashing
        inactivityThreshold = 30 days;

        if (_stakingToken != address(0)) {
            stakingToken = IERC20(_stakingToken);
        }
    }

    /**
     * @dev Register as a verifier with required stake
     * @param metadata IPFS hash or JSON metadata about the verifier
     */
    function registerVerifier(string calldata metadata) 
        external 
        payable 
        whenNotPaused 
        nonReentrant 
    {
        require(!verifiers[msg.sender].isActive, "Already registered as verifier");
        require(bytes(metadata).length > 0, "Metadata cannot be empty");

        uint256 stakeAmount;

        if (address(stakingToken) == address(0)) {
            // Native HBAR staking
            require(msg.value >= minimumStake, "Insufficient stake amount");
            stakeAmount = msg.value;
        } else {
            // ERC20 token staking
            require(msg.value == 0, "No native currency for token staking");
            stakeAmount = minimumStake;
            require(
                stakingToken.transferFrom(msg.sender, address(this), stakeAmount),
                "Stake transfer failed"
            );
        }

        verifiers[msg.sender] = Verifier({
            verifierAddress: msg.sender,
            stakedAmount: stakeAmount,
            reputationScore: 5000, // Start with neutral reputation (50%)
            totalVerifications: 0,
            successfulVerifications: 0,
            lastActivityTimestamp: block.timestamp,
            isActive: true,
            metadata: metadata,
            registrationTimestamp: block.timestamp
        });

        verifierList.push(msg.sender);

        emit VerifierRegistered(msg.sender, stakeAmount, metadata);
    }

    /**
     * @dev Increase stake amount
     */
    function increaseStake() 
        external 
        payable 
        whenNotPaused 
        nonReentrant 
    {
        require(verifiers[msg.sender].isActive, "Not a registered verifier");

        uint256 additionalAmount;

        if (address(stakingToken) == address(0)) {
            require(msg.value > 0, "Must send HBAR to increase stake");
            additionalAmount = msg.value;
        } else {
            require(msg.value == 0, "No native currency for token staking");
            additionalAmount = minimumStake; // Allow fixed increments
            require(
                stakingToken.transferFrom(msg.sender, address(this), additionalAmount),
                "Stake transfer failed"
            );
        }

        verifiers[msg.sender].stakedAmount += additionalAmount;

        emit VerifierStakeIncreased(
            msg.sender, 
            additionalAmount, 
            verifiers[msg.sender].stakedAmount
        );
    }

    /**
     * @dev Calculate verification fee based on verifier reputation
     * @param verifier Address of the verifier
     * @return fee The calculated fee
     */
    function calculateVerificationFee(address verifier) 
        public 
        view 
        returns (uint256 fee) 
    {
        require(verifiers[verifier].isActive, "Verifier not active");

        uint256 reputation = verifiers[verifier].reputationScore;

        // Higher reputation = higher fee (premium pricing)
        // Fee = baseVerificationFee * (1 + (reputation - 5000) * reputationMultiplier / 10000 / 10000)
        int256 reputationAdjustment = (int256(reputation) - 5000) * int256(reputationMultiplier) / 10000;

        if (reputationAdjustment >= 0) {
            fee = baseVerificationFee + (baseVerificationFee * uint256(reputationAdjustment) / 10000);
        } else {
            uint256 reduction = baseVerificationFee * uint256(-reputationAdjustment) / 10000;
            fee = reduction >= baseVerificationFee ? baseVerificationFee / 2 : baseVerificationFee - reduction;
        }
    }

    /**
     * @dev Complete a verification and update reputation
     * @param requestId Unique identifier for the verification request
     * @param verifier Address of the verifier
     * @param successful Whether the verification was successful
     */
    function completeVerification(
        bytes32 requestId,
        address verifier,
        bool successful
    ) 
        external 
        onlyRole(ADMIN_ROLE) 
        whenNotPaused 
    {
        require(verifiers[verifier].isActive, "Verifier not active");
        require(!verificationRequests[requestId].completed, "Verification already completed");

        verificationRequests[requestId].completed = true;
        verificationRequests[requestId].successful = successful;

        // Update verifier stats
        verifiers[verifier].totalVerifications++;
        verifiers[verifier].lastActivityTimestamp = block.timestamp;

        if (successful) {
            verifiers[verifier].successfulVerifications++;
        }

        // Update reputation score
        _updateReputation(verifier);

        // Pay verifier
        uint256 fee = verificationRequests[requestId].fee;
        verifierEarnings[verifier] += fee;

        emit VerificationCompleted(requestId, verifier, successful, fee);
    }

    /**
     * @dev Slash a verifier for fraud or inactivity
     * @param verifier Address of the verifier to slash
     * @param reason Reason for slashing
     */
    function slashVerifier(address verifier, string calldata reason) 
        external 
        onlyRole(SLASHER_ROLE) 
        whenNotPaused 
        nonReentrant 
    {
        require(verifiers[verifier].isActive, "Verifier not active");

        uint256 slashAmount = verifiers[verifier].stakedAmount * slashingPercentage / 10000;
        verifiers[verifier].stakedAmount -= slashAmount;

        // If stake falls below minimum, deactivate verifier
        if (verifiers[verifier].stakedAmount < minimumStake) {
            verifiers[verifier].isActive = false;
            emit VerifierDeactivated(verifier, "Stake below minimum after slashing");
        }

        // Reduce reputation significantly
        uint256 oldScore = verifiers[verifier].reputationScore;
        verifiers[verifier].reputationScore = verifiers[verifier].reputationScore * 7 / 10; // 30% reduction

        emit VerifierSlashed(verifier, slashAmount, reason);
        emit ReputationUpdated(verifier, oldScore, verifiers[verifier].reputationScore);
    }

    /**
     * @dev Withdraw earnings
     */
    function withdrawEarnings() 
        external 
        whenNotPaused 
        nonReentrant 
    {
        uint256 earnings = verifierEarnings[msg.sender];
        require(earnings > 0, "No earnings to withdraw");

        verifierEarnings[msg.sender] = 0;

        if (address(stakingToken) == address(0)) {
            payable(msg.sender).transfer(earnings);
        } else {
            require(stakingToken.transfer(msg.sender, earnings), "Transfer failed");
        }

        emit EarningsWithdrawn(msg.sender, earnings);
    }

    /**
     * @dev Get verifier information
     * @param verifier Address of the verifier
     * @return Verifier struct
     */
    function getVerifier(address verifier) 
        external 
        view 
        returns (Verifier memory) 
    {
        return verifiers[verifier];
    }

    /**
     * @dev Get all active verifiers
     * @return Array of active verifier addresses
     */
    function getActiveVerifiers() 
        external 
        view 
        returns (address[] memory) 
    {
        uint256 activeCount = 0;

        // Count active verifiers
        for (uint256 i = 0; i < verifierList.length; i++) {
            if (verifiers[verifierList[i]].isActive) {
                activeCount++;
            }
        }

        // Create array of active verifiers
        address[] memory activeVerifiers = new address[](activeCount);
        uint256 index = 0;

        for (uint256 i = 0; i < verifierList.length; i++) {
            if (verifiers[verifierList[i]].isActive) {
                activeVerifiers[index] = verifierList[i];
                index++;
            }
        }

        return activeVerifiers;
    }

    /**
     * @dev Check for inactive verifiers and deactivate them
     */
    function checkInactiveVerifiers() 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        for (uint256 i = 0; i < verifierList.length; i++) {
            address verifier = verifierList[i];
            if (verifiers[verifier].isActive && 
                block.timestamp - verifiers[verifier].lastActivityTimestamp > inactivityThreshold) {

                verifiers[verifier].isActive = false;
                emit VerifierDeactivated(verifier, "Inactivity threshold exceeded");
            }
        }
    }

    /**
     * @dev Update reputation score based on success rate
     * @param verifier Address of the verifier
     */
    function _updateReputation(address verifier) internal {
        uint256 total = verifiers[verifier].totalVerifications;
        uint256 successful = verifiers[verifier].successfulVerifications;

        if (total == 0) return;

        uint256 oldScore = verifiers[verifier].reputationScore;

        // Calculate success rate (0-10000 basis points)
        uint256 successRate = (successful * 10000) / total;

        // Weighted average with existing reputation (70% old, 30% new)
        uint256 newScore = (oldScore * 7 + successRate * 3) / 10;

        // Ensure score stays within bounds
        if (newScore > 10000) newScore = 10000;

        verifiers[verifier].reputationScore = newScore;

        emit ReputationUpdated(verifier, oldScore, newScore);
    }

    /**
     * @dev Admin functions for updating parameters
     */
    function updateMinimumStake(uint256 _minimumStake) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        minimumStake = _minimumStake;
    }

    function updateBaseVerificationFee(uint256 _baseVerificationFee) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        baseVerificationFee = _baseVerificationFee;
    }

    function updateReputationMultiplier(uint256 _reputationMultiplier) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        reputationMultiplier = _reputationMultiplier;
    }

    function updateSlashingPercentage(uint256 _slashingPercentage) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        require(_slashingPercentage <= 5000, "Slashing percentage too high"); // Max 50%
        slashingPercentage = _slashingPercentage;
    }

    function updateInactivityThreshold(uint256 _inactivityThreshold) 
        external 
        onlyRole(ADMIN_ROLE) 
    {
        inactivityThreshold = _inactivityThreshold;
    }

    /**
     * @dev Pause/unpause contract
     */
    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @dev Authorize upgrade
     */
    function _authorizeUpgrade(address newImplementation) 
        internal 
        onlyRole(UPGRADER_ROLE) 
        override 
    {}

    /**
     * @dev Emergency withdrawal function (only admin)
     */
    function emergencyWithdraw() 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
        nonReentrant 
    {
        if (address(stakingToken) == address(0)) {
            payable(msg.sender).transfer(address(this).balance);
        } else {
            uint256 balance = stakingToken.balanceOf(address(this));
            require(stakingToken.transfer(msg.sender, balance), "Transfer failed");
        }
    }

    /**
     * @dev Receive function for native currency deposits
     */
    receive() external payable {
        // Allow contract to receive native currency
    }
}

